
var url;function newModel(){$('#dlg').dialog('open').dialog('center').dialog('setTitle','New Model');$('#fm').form('clear');url='save_model';}
function editModel(){var row=$('#dg').datagrid('getSelected');if(row){$('#dlg').dialog('open').dialog('center').dialog('setTitle','Edit Model');$('#fm').form('load',row);url='update_model?id='+row.id;}}
function saveModel(){$('#fm').form('submit',{url:url,onSubmit:function(){return $(this).form('validate');},success:function(result){var result=eval('('+result+')');if(result.errorMsg){$.messager.show({title:'Error',msg:result.errorMsg});}else{$('#dlg').dialog('close');$('#dg').datagrid('reload');}}});}
function destroyModel(){var row=$('#dg').datagrid('getSelected');if(row){$.messager.confirm('Confirm','Are you sure you want to destroy this user?',function(r){if(r){$.post('destroy_model',{id:row.id},function(result){if(result.success){$('#dg').datagrid('reload');}else{$.messager.show({title:'Error',msg:result.errorMsg});}},'json');}});}}